import React, { useContext } from "react";
import { GlobalContext } from "../context/GlobalState";

const IncomeExpenses = () => {
	const { transactions } = useContext(GlobalContext);

	const amounts = transactions.map((transaction) => transaction.amount);
	console.log(amounts);

	const plus = amounts.filter((amount) => amount > 0);
	console.log(plus);

	const totalPlus = plus.reduce((acc, item) => (acc += item), 0).toFixed(2);
	console.log(totalPlus);

	const minus = amounts.filter((amount) => amount < 0);
	console.log(minus);

	const totalMinus = minus.reduce((acc, item) => (acc += item), 0).toFixed(2);

	return (
		<div className="inc-exp-container">
			<div>
				<h4>Income</h4>
				<p className="money plus">${totalPlus}</p>
			</div>
			<div>
				<h4>Expense</h4>
				<p className="money minus">${Math.abstotalMinus}</p>
			</div>
		</div>
	);
};

export default IncomeExpenses;
